import React from 'react';

import './header.styles.scss';
import { auth } from './../../firebase/firebase.utils';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import { ReactComponent as Logo } from '../../assets/bs_prints_logo.svg';
import CartIcon from '../cart-icon/cart-icon.component';
import CartDropdown from '../cart-dropdown/cart-dropdown.component';
import { selectCartHidden } from '../../redux/cart/cart.selectors';
import { selectCurrentUser } from '../../redux/user/user.selector';
import { LogoContainer, OptionsContainer, OptionLink, OptionDiv, HeaderContainer } from './header.styles';

const Header = ({ currentUser, hidden }) => (
  <HeaderContainer className='header'>
    <LogoContainer className='logo-container' to="/" >
      <Logo className='logo' />
    </LogoContainer>
    <OptionsContainer className='options'>
      <OptionLink className='option' to="/shop">
        SHOP
      </OptionLink>
      <OptionLink className='option' to="/shop">
        CONTACT
      </OptionLink>
      {currentUser ? (
        <OptionDiv className='option' onClick={() => auth.signOut()} >
          SIGN OUT
        </OptionDiv>
      ) : (
          <OptionLink className='option' to="/signin">
            SIGN IN
          </OptionLink>
        )}
      <CartIcon />
    </OptionsContainer>
    {
      hidden ? null :
        <CartDropdown />
    }
  </HeaderContainer>
);

const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser,
  hidden: selectCartHidden
});

export default connect(mapStateToProps)(Header);
