import React from 'react';
import { connect } from 'react-redux';
import { addItem } from './../../redux/cart/cart.actions';

import './collection-item.styles.scss';
import CustomButton from '../custom-button/custom-button.component';
import { CollectionItemContainer, CollectionImageContainer, CollectionFooterContainer, CollectionItemName, CollectionItemPrice, AddToCartButtonContainer } from './collection-item.styles';

const CollectionItem = ({ item, addItem }) => {
  const { name, price, imageUrl } = item; 

  return (
  <CollectionItemContainer >
    <CollectionImageContainer style={{ backgroundImage : `url(${imageUrl})`}} />
    <CollectionFooterContainer >
      <CollectionItemName>{ name }</CollectionItemName>
      <CollectionItemPrice>{ price }</CollectionItemPrice>
    </CollectionFooterContainer>
    <AddToCartButtonContainer onClick={() => addItem(item)} inverted >Add to cart</AddToCartButtonContainer>
  </CollectionItemContainer>
)};

const mapDispatchToProps = dispatch => ({
  addItem: item => dispatch(addItem(item))
})

export default connect(null, mapDispatchToProps)(CollectionItem);
